import React, { Fragment } from "react";
import { Link } from "react-router-dom";
import IcoMoon from "react-icomoon";

function Task({ task, toggle, props }) {
  function handleSubmit(e) {
    e.preventDefault();
    edit ? updateTask(formValues.id) : addTask();
    history.push("/");
  }

  function handleCancel() {
    history.push("/");
  }
  return (
    <Fragment>
      {" "}
      {/* <> */}
      <li onClick={() => toggle(task.id)}>
        {task.completed ? <del>{`${task.task}`}</del> : `${task.task}`}
      </li>
      <button className="edit-style">
        <Link to={`/edittask/${task.id}`}>
          {" "}
          <IcoMoon icon="pencil2" />
        </Link>
      </button>
      <form onSubmit={handleSubmit}>
        <input name="task" value={formValues.task} onChange={handleChange} />
        <button type="submit">Submit</button>
        <button onClick={handleCancel}>Cancel</button>
      </form>
      <button className="delete-style" onClick={props.delete}>
        <IcoMoon icon="bin" />
      </button>
    </Fragment>
  );
}

export default Task;
