import React, { Fragment } from "react";
import { Link } from "react-router-dom";

function Task(props) {
  return (
    <Fragment>
      {" "}
      {/* <> */}
      <li onClick={() => toggle(task.id)}>
        {task.completed ? <del>{`${task.task}`}</del> : `${task.task}`}
      </li>
      <button className="edit-style">
        <Link to={`/edittask/${task.id}`}>Edit</Link>
      </button>
      <button className="delete-style" onClick={props.delete}>
        Delete Task
      </button>
    </Fragment>
  );
}

export default Task;
