import React, { Fragment, useState, useEffect } from "react";
import { Link } from "react-router-dom";
import IcoMoon from "react-icomoon";
function Task({ task, toggle, props, setTasks }) {
  const [formValues, setFormValues] = useState({
    id: Date.now(),
    task: "",
    completed: false
  });

  return (
    <Fragment>
      {" "}
      {/* <> */}
      <li onClick={() => toggle(task.id)}>
        {task.completed ? <del>{`${task.task}`}</del> : `${task.task}`}
      </li>
      <button className="edit-style">
        <Link to={`/edittask/${task.id}`}>
          {" "}
          <IcoMoon icon="pencil2" />
        </Link>
      </button>
      <button className="delete-style" onClick={props.delete}>
        <IcoMoon icon="bin" />
      </button>
    </Fragment>
  );
}

export default Task;
